INSERT INTO `post` (`id`, `title`, `content`, `tbtime`, `user_create`, `cate_post`) VALUES
(2, 'Nhà trường kết hợp với Doanh nghiệp trong quá trình Đào tạo và Giảng dạy', '\r\n                                            <p></p><p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Với mô hình đào tạo Học - hành - nghề - nghiệp, gắn kết hài hòa giữa học tập, nghiên cứu, giảng dạy, huấn luyện, thực hành, trải nghiệm và hướng nghiệp, sinh viên Đại học Thành Đô sau khi tốt nghiệp ra trường đều có việc làm đúng chuyên ngành và ổn định. </span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span style=\"color:black\"><img alt=\"\" src=\"http://quantri.thanhdo.edu.vn/upload/thanhdo/images/m%C3%B4%20h%C3%ACnh%20%C4%90%C3%A0o%20t%E1%BA%A1o%20tr%C6%B0%E1%BB%9Dng%20%C4%90%E1%BA%A1i%20h%E1%BB%8Dc%20Th%C3%A0nh%20%C4%90%C3%B4.png\" style=\"height:430px; width:555px\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/mo%20hinh%20dao%20tao%20-%20TDU.jpg\" style=\"height:430px; width:555px\"></span></strong><br>\r\n<em><span style=\"color:black\">(Mô hình đào tạo Trường Đại học Thành Đô)</span></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><a href=\"https://docs.google.com/forms/d/1KE5LztuHOim3pvE-QTtPy3tUJtiGTCwZiImO93dbPZk/viewform?fbclid=IwAR3esJ_40tPhO1GI28jON3LpIjJ7d9Ycl48HLLyBf5BGg08IOUEoA8tdsQ0&amp;edit_requested=true\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/dangkyngay.jpg\" style=\"height:41px; width:190px\"></a><br>\r\n<a href=\"https://docs.google.com/forms/d/e/1FAIpQLScB2Qzc90z5DppbqpqXD0tyNNkGlN8-QvR3NaI4NUb4MotS7Q/viewform\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">(Đăng ký học tập tại Đại học Thành Đô)</span></span></a></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Với việc ký kết&nbsp;hợp tác và đào tạo với hơn 600&nbsp;doanh nghiệp trong nước và rất nhiều trường đại học quốc tế, sinh viên Đại học Thành Đô sẽ được tham gia các kỳ thực tập tại các cơ sở doanh nghiệp hàng đầu trên khắp cả nước ngay từ những năm đầu tiên, do dó cơ hội việc làm sau khi ra trường cho sinh viên Thành Đô là rất lớn. Ngoài ra, cơ hội du học dành cho sinh viên của trường cũng rất thuận lợi. </span></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span style=\"color:#d35400\">Các Tập đoàn hiện đang là đối tác của Đại học Thành Đô trong đào tạo, giảng dạy và tuyển dụng:</span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;&nbsp;<img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/Huyndai%20Th%C3%A0nh%20An.jpg\" style=\"height:182px; width:300px\"><br>\r\n<em><strong><span style=\"color:#004d86\">(Công ty cổ phần tập đoàn Thành An (HuynDai Thành An)</span></strong></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><br>\r\n<span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/DN01.jpg\" style=\"height:197px; width:300px\"><img alt=\"\" src=\"http://quantri.thanhdo.edu.vn/upload/thanhdo/images/image007.png\" style=\"height:197px; width:300px\"><br>\r\n<em><strong><span style=\"color:#365f91\">(Tập đoàn Ô tô Toyota VN -&nbsp;Chi nhánh Phúc Yên )</span></strong></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/DN02.jpg\" style=\"height:120px; width:300px\"><img alt=\"\" src=\"http://quantri.thanhdo.edu.vn/upload/thanhdo/images/Logo%20T%E1%BA%ADp%20%C4%91o%C3%A0n%20samsung%20vina.png\" style=\"height:121px; width:300px\">&nbsp;<br>\r\n<em><strong><span style=\"color:#365f91\">(Công ty Điện tử Samsung Vina)</span></strong></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span style=\"color:#365f91\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/DN04.jpg\" style=\"height:200px; width:300px\">&nbsp;</span></strong><br>\r\n<em><strong><span style=\"color:#004d86\">(Công ty TNHH Sản phẩm Ricoh Imaging)</span></strong></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong><span style=\"color:#004d86\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/thanhdo/images/DN05.jpg\" style=\"height:298px; width:300px\"><img alt=\"\" src=\"http://quantri.thanhdo.edu.vn/upload/thanhdo/images/Logo%20T%E1%BA%ADp%20%C4%91o%C3%A0n%20Athena.png\" style=\"height:299px; width:300px\">&nbsp;</span></strong><br>\r\n<em><strong><span style=\"color:#004d86\">(Công ty cổ phần công nghệ Athena Việt Nam)</span></strong></em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"https://quantri.thanhdo.edu.vn/upload/tha